# methods
def translate_dna_to_protein(dna_sequence, log=False):
    protein_sequence = ""

    # log
    if log:
        print("got dna of length: {}".format(len(dna_sequence)))

    # split the string
    for i in range(0, len(dna_sequence), 3):
        codon = dna_sequence[i:i+3]

        # if log:
        #     print("codon: {}".format(codon))
        if codon in codon_table:
            protein_sequence += codon_table[codon]
        else:
            protein_sequence += 'X'  # Unknown codon

    # return
    return protein_sequence

def filter_string_by_letters(input_string, list_letters=['A', 'C', 'G', 'T']):
    # Convert the list of letters to a set for faster lookup
    letters_set = set(list_letters)
    
    # Use a list comprehension to filter the input string
    filtered_string = ''.join([char for char in input_string if char in letters_set])
    
    return filtered_string

